    public static EnrolmentFormSubmission getEnrolmentFormTestSubmission() {

        EnrolmentFormSubmission submission = new EnrolmentFormSubmission();

        submission.setPrimaryDetails(getPrimaryDetails());
        submission.setMemberDetails(getMemberDetails());
        submission.setPaymentDetails(getPaymentDetails());
//        HashMap<MembershipDetails, int[]> membershipsMap = getMembershipDetails();
        HashMap<MembershipDetails, int[]> membershipsMap = getMembershipDetails_v2();
        membershipsMap.forEach((key,value) -> submission.setMembershipDetails(key));
        submission.setHealthCheck(getHealthCheck());
        submission.setLegalDetails(getLegalDetails());
        submission.setPrimarySignatureURL(primarySignatureURL_1);
        submission.setPaymentAuthSignatureURL(paymentAuthSignatureURL_1);
        submission.setUnder18SignatureURL(null);
        membershipsMap.forEach((key, value) -> submission.setMemberContracts(value));
        submission.setExistingClient(existingClient);
        submission.setFirstBillingDate(getFirstBillingDate(submission.getMembershipDetails().getDaysFree()));
        submission.setCreateDate(Helpers.getDateNow());
        submission.setLocationId(getLocationId());
        submission.setUID(UID);

        logger.info("Test Submission \n{}",submission);

        return submission;
    }

