    public static String getFirstBillingDate(String daysFree) {

        switch (daysFree) {
            case Constants.COUPON_CODE_7_DAYS:
                return Helpers.getDatePlusNDays(7);
            case Constants.COUPON_CODE_14_DAYS:
                return Helpers.getDatePlusNDays(14);
            case Constants.COUPON_CODE_30_DAYS:
                return Helpers.getDatePlusNDays(30);
            default:
                return Helpers.getDatePlusNDays(0);
        }
    }

    public static String getLocationId() {

        int loc = getRandomNumber(5);

        while (loc == 4 || loc == 0) {
            loc = getRandomNumber(5);
        }

        return String.valueOf(loc * -1);
    }

